import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faUser, faTicket, faHouse, faArrowLeft, faCog, faChartPie , faBinoculars, faPencil , faEdit, faEye} from '@fortawesome/free-solid-svg-icons'; // Import the specific icons you need
import $ from 'jquery';
import '../dashboard.css'
import '../index.css'

function Alltick() {
  const [error, setError] = useState('');
  const [allinfo, setAllInfo] = useState([]);
  const navigate = useNavigate();


  console.log("this is all info", allinfo)

  useEffect(() => {
    // Fetch ticket information
    $.ajax({
      url: 'http://127.0.0.1:8000/api/tickets/all',
      method: 'POST',
      contentType: 'application/json',
      success: (response) => {
        if (response.success) {
          console.log('this is data of all tickes', response.latest_tik.latest_tik);
          setAllInfo(response.latest_tik.latest_tik.latest_tik); // Update state with fetched data
        } else {
          setError('Error fetching records');
        }
      },
      error: (xhr, status, error) => {
        setError('Error fetching records');
        console.error('Error fetching records:', error);
      }
    });
  }, []); // Empty dependency array ensures the effect runs only once, equivalent to componentDidMount()

  return (
    <>
        < Menu></Menu>

      <div className="contenttable">
        <h5>Recent Tickets</h5>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Description</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {allinfo.map((item) => (
              <tr key={item.id}>
                <td>{item.name}</td>
                <td>{item.description}</td>
                <td>{item.status}</td>
                <td>
                  <button>View</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
}

export default Alltick;
