import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faUser, faTicket,faTrash,  faHouse, faArrowLeft, faCog, faChartPie , faBinoculars, faPencil , faEdit, faEye} from '@fortawesome/free-solid-svg-icons'; // Import the specific icons you need
import $ from 'jquery';
import '../dashboard.css'
import '../index.css'
import Menu from './menu'

function Alltick() {
  const [error, setError] = useState('');
  const [allinfo, setAllInfo] = useState([]);
  const navigate = useNavigate();





  console.log("this is all info", allinfo)

  useEffect(() => {
    // Fetch ticket information
    $.ajax({
      url: 'http://127.0.0.1:8000/api/tickets/all',
      method: 'POST',
      contentType: 'application/json',
      success: (response) => {
        if (response.success) {
          console.log('this is data of all tickes', response.latest_tik.latest_tik);
          setAllInfo(response.latest_tik.latest_tik.latest_tik); // Update state with fetched data
        } else {
          setError('Error fetching records');
        }
      },
      error: (xhr, status, error) => {
        setError('Error fetching records');
        console.error('Error fetching records:', error);
      }
    });
  }, []); // Empty dependency array ensures the effect runs only once, equivalent to componentDidMount()

  return (
    <>
        < Menu></Menu>

      <div className="contenttable">
        <div className="title-info">
                <p>dashboard ,<b> Welcome </b></p>
                <i className="fas fa-chart-bar"></i>
        </div>
        <h5>Recent Tickets</h5>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Description</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {allinfo.map((item) => (
              <tr key={item.id}>
                <td>{item.name}</td>
                <td>{item.description}</td>
                <td>{item.status}</td>
                <td > 
                    <FontAwesomeIcon onClick={() => handleDelete(item.id)} className="tabicon" icon={faTrash} />
                    <FontAwesomeIcon className="tabicon" icon={faPencil} />
                    <FontAwesomeIcon className="tabicon" icon={faEye} />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
}


function handleDelete(id){

    console.log("this is a record id ", id )

    $.ajax({
        url: 'http://127.0.0.1:8000/api/tickets/delete',
        method: 'POST',
        contentType: 'application/json',
        data: JSON.stringify({ id }),
        success: (response) => {
          if (response.success) {

            //call a snackbar 

            console.log(response)
          } else {
            // setError('Error deleting records');
          }
        },
        error: (xhr, status, error) => {
          setError('Error deleting records');
          console.error('Error deleting records:', error);
        }
      });



  }
export default Alltick;
